query PublishedCohortLibraryGetBySlugName {
    PublishedCohortLibraryGetBySlugName(urlSlug: "flu-22-to-23") {
        ... on Cohort {
            id
            name
            urlSlug
            conditionGroups {
                id
                name
                urlSlug
            }
        }
    }
}
